sql.where:
bpm.isMain=1
AND bpm.subject  LIKE #{subject}  
AND type.typeName LIKE #{typeName} 
AND type.nodePath LIKE #{nodePath} 
AND bpm.defKey  LIKE #{defKey}  
AND bpm.taskNameRule  LIKE #{taskNameRule}  
AND bpm.descp  LIKE #{descp}  
AND bpm.createtime  >=#{createtime,jdbcType=DATE} 
AND bpm.createtime <=#{endcreatetime,jdbcType=DATE} 
AND bpm.status  =#{status} 
AND bpm.isUseOutForm  =#{isUseOutForm} 
AND bpm.typeId  =#{typeId}

mapper.select:
select bpm.*,type.typeName typeName from BPM_DEFINITION bpm left join SYS_GL_TYPE type on bpm.typeId=type.typeId
order by ${orderField} ${orderSeq}
order by defId desc

select  
from BPM_DEFINITION def

	,SYS_GL_TYPE type where def.typeId=type.typeId
	and type.nodePath like #{nodePath}


	left join SYS_GL_TYPE type on def.typeId=type.typeId
	where 1=1


 AND createBy  like #{createBy} 
 AND (subject LIKE #{searchCondition}  OR  createtime LIKE #{searchCondition} OR createBy  LIKE #{searchCondition} )


	order by def.${orderField} ${orderSeq}


	order by def.defId desc

select def.typeId,count(DISTINCT def.defId) total from BPM_DEFINITION def
,SYS_GL_TYPE type where def.typeId=type.typeId and type.nodePath like #{nodePath}
GROUP BY def.typeId

select a.*,b.typeName from BPM_DEFINITION a,sys_gl_type b
where a.typeId=b.typeId and a.parentDefId=#{parentDefId}
order by a.versionNo desc

select a.*,b.typeName from BPM_DEFINITION a,sys_gl_type b
where a.typeId=b.typeId and a.parentDefId=#{parentDefId} and a.isMain=#{isMain}
order by a.versionNo desc

