mapper.update:
UPDATE BPM_DEFINITION SET  typeId=#{typeId} where defKey
in 
	#{defKey}

mapper.select:
select  from BPM_DEFINITION def left join SYS_GL_TYPE type on type.typeId=def.typeId 

	AND def.status in (1,4)  
	AND def.ismain=1



	 		AND def.defKey in(${actRights})  


	 		AND def.defKey = ''  


	 and type.nodePath like #{nodePath} 
	 AND def.subject LIKE #{subject} ESCAPE '|'  
	 AND def.defKey  LIKE #{defKey} ESCAPE '|'  
	 AND def.descp  LIKE #{descp} ESCAPE '|' 
	 AND def.createtime  >=#{createtime,jdbcType=DATE} 
	 AND def.createtime <=#{endcreatetime,jdbcType=DATE} 
	 AND def.status  =#{status} 
	 AND (def.status  =1 or def.status  =4)  
	 and type.nodeKey in (${nodeKey}) 
	 and (type.nodeKey not in (${notNodeKey}) or type.nodeKey is NULL) 
	 and def.allowMobile = #{allowMobile} 

		AND def.typeId IN

			#{typeId}





	order by ${orderField} ${orderSeq}



	order by  def.subject, def.createtime desc

SELECT distinct actDefId
FROM bpm_node_set
WHERE formKey=#{formKey}
