sql.where:
 AND t.POSID  =#{posId} 
 AND t.POSNAME  LIKE #{posName}  
 AND t.POSDESC  LIKE #{posDesc}  
 AND t.POSCODE  LIKE #{posCode}  
 AND t.ORGID  =#{orgId} 
 AND t.JOBID  =#{jobId} 
AND t.ISDELETE=0

mapper.select:
SELECT ,o.orgName,j.jobName FROM SYS_POS t left join sys_org o on o.orgId=t.orgId left join sys_job j on j.jobId=t.jobId
order by t.${orderField} ${orderSeq}
order by t.POSID desc

SELECT

FROM SYS_POS t
WHERE t.posName = #{posName}

		SELECT ,j.jobName,d.itemname jobGradeName
		FROM SYS_POS t 
		left join sys_job j on t.jobid = j.jobid
		left join (
		select * from SYS_DIC where typeid in (
    		select typeid from SYS_GL_TYPE where catkey = 'DIC' and nodekey = '${nodekey}' AND enterprise_code = #{orgCode}
   			)
		)d on j.grade = d.itemvalue
		where  t.isdelete = 0

			and t.ORGID in(SELECT o.orgId FROM sys_org o where o.path like #{orgPath} and o.isdelete = 0 AND o.ORGCODE=#{orgCode})

		 AND t.ORGID  =#{orgId} 
		 AND t.POSNAME  LIKE #{posName}  
		 AND (t.POSNAME  LIKE #{searchCondition} OR j.jobName LIKE #{searchCondition} OR d.itemname LIKE #{searchCondition})
		order by t.POSID  desc
