sql.where:
 AND setId  =#{setId} 
 AND defId  =#{defId} 
 AND nodeName  LIKE #{nodeName}  
 AND actDefId  =#{actDefId} 
 AND parentActDefId  =#{parentActDefId} 
 AND nodeId  LIKE #{nodeId}  
 AND formType  =#{formType} 
 AND formUrl  LIKE #{formUrl}  
 AND formKey  =#{formKey} 
 AND formDefName  =#{formDefName} 
 AND scope  LIKE #{scope}  
 AND mobileFormKey  =#{mobileFormKey} 
AND setType =0

mapper.select:
SELECT FROM BPM_NODE_SET
order by ${orderField} ${orderSeq}
order by nodeOrder desc

SELECT 
FROM BPM_NODE_SET
WHERE actDefId=#{actDefId} and nodeId=#{nodeId} and parentActDefId=#{parentActDefId}

mapper.update:
UPDATE bpm_node_set SET
scope=#{scope,jdbcType=VARCHAR}
WHERE
setId=#{setId}

UPDATE bpm_node_set SET
communicate=#{communicate,jdbcType=CLOB}
WHERE
setId=#{setId}

mapper.select:
SELECT FROM bpm_node_set WHERE defId=#{defId} and setType=0 and (OPINIONFIELD is not null or OPINIONFIELD!='')
and (parentActDefId is null or parentActDefId='')
AND parentActDefId=#{parentActDefId}
